import React from 'react';
import StepOne from './StepOne';

class MainStep extends React.Component{
    state = {
        step:1,
        //for step one
        channel:null,
        channelType:[],
        trackingOption:[],
        //for step two
        startDate:'',
        endDate:''
    }

    showStepDetails = () => {
        const {step} = this.state;
        if(step === 1){
            return <StepOne />
        }
    }

    render(){
        return(
            <div>Main file</div>
        );
    };
}

export default MainStep;
