import React from 'react';
import InfoIcon from '@material-ui/icons/Info';
import '../Style/StepOne.css'
import CheckBox from './CheckBox';
import ChannelType from './ChannelType';

class StepOne extends React.Component{

    gotoNextStep = (event) =>{
        event.preventDefault();
        this.props.nextStep();
    }

    getCheckedValue = (value) =>{
         this.props.channelType.map((element) =>{
            //console.log(element);
            if(element.type === value)
                return element.value;
        });
    }

    render(){
        return(
            <div>
                <h4>
                    <InfoIcon color='primary' style={{ fontSize: 30, position: 'relative', top: 10, right: 5 }} />
                    Select the channel of your campaign
                </h4>
                <div className="flex-container-camp">
                    <ChannelType iconType='digital' text='Digital' onChangeChannel={this.props.onChangeChannel} />
                    <ChannelType iconType='OutOfHome' text='Out Of Home' onChangeChannel={this.props.onChangeChannel} />
                    <ChannelType iconType='tv' text='TV' onChangeChannel={this.props.onChangeChannel} />
                    <ChannelType iconType='radio' text='Radio' onChangeChannel={this.props.onChangeChannel} />
                </div>
                <h4>TYPE(S) OF CHANNEL</h4>
                <div className="flex-container-channel">
                    <CheckBox label="In App" itemType="channel" checkedChannelValue = {this.getCheckedValue(('In App'))} onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Mobile Web" itemType="channel" checkedChannelValue = {this.props.channelType} onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Desktop" itemType="channel" checkedChannelValue = {this.props.channelType} onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Social Media" itemType="channel" checkedChannelValue = {this.props.channelType} onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                </div>
                <div className="flex-container-channel">
                    <CheckBox label="Paid Search" itemType="channel" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Email" itemType="channel" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Lead Form" itemType="channel" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Landing Page" itemType="channel" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                </div>
                <h4>TRACKING OPTIONS</h4>
                <label>Attribution Type</label>
                <div className="flex-container-channel">
                    <CheckBox label="Visits" itemType="attribution" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                    <CheckBox label="Transactions" itemType="attribution" onChangeChannelType={this.props.onChangeChannelType}></CheckBox>
                </div>
                <button onClick = {this.gotoNextStep}>Next >></button>
            </div>
        );
    }
}

export default StepOne;
