import React from 'react';
import InfoIcon from '@material-ui/icons/Info';
import '../Style/StepOne.css'
import '../Style/StepThree.css'

class StepThree extends React.Component {
    state = {
        amount: "0.00"
    }
    getInitialState() {
        return ({ amount: "0.00" });
    }
    handleChange(event, maskedvalue, floatvalue) {
        console.log(maskedvalue)
    }
    render() {
        return (
            <div>
                <h4>
                    <InfoIcon color='primary' style={{ fontSize: 30, position: 'relative', top: 10, right: 5 }} />
                    How much is your campaign budget?
                </h4>
                <div className='ui grid'>
                    <div className='four wide column'>
                        <h3>Total Budget</h3>
                        <div className="ui right labeled input">
                            <div className="ui basic label">US$</div>
                            <input type='text' className="set-cuurency-box-budget" />
                            <div className="ui label">.00</div>
                        </div>
                    </div>
                    <div className='four wide column'>
                        <h3>Total Impressions</h3>
                        <input type='text' className="set-cuurency-box-impression" />
                    </div>
                </div>
                <button onClick = {this.gotoPrevStep}>Previous</button>
            </div>
    
        );
    }
}

export default StepThree;
