import React from 'react';
import StepOne from './StepOne';

class MainStep extends React.Component {

    constructor(props){
        super(props)
        this.state = {
        step: 1,
        //for step one
        channel: null,
        channelType: [],
        trackingOption: [],
        //for step two
        startDate: '',
        endDate: ''
    }
}

    showStepDetails = () => {
        const { step } = this.state;
        if (step === 1) {
            return <StepOne  onChangeChannel = {this.onChangeChannel} onChangeChannelType = {this.onChangeChannelType}/>
        }
    }

    nextStep = () =>{
        const {step} = this.state;
        this.setState({step : step + 1});
    }

    prevStep = () =>{
        const {step} = this.state;
        this.setState({step : step - 1});
    }

    onChangeChannel = (event) => {
        console.log(event.target.id);
        this.setState({channel: event.target.id});
    }

    onChangeChannelType = (event) => {
        if (event.target.checked) {
            event.target.id === 'channel' ? 
            this.setState({channelType: [...this.state.channelType, event.target.name]}) : 
            this.setState({trackingOption:[...this.state.trackingOption, event.target.name]});
        }
        else {
            if (event.target.id === 'channel') {
                //var index = this.stepOneObject.channelType.indexOf(event.target.name);
                //if (index !== -1) this.stepOneObject.channelType.splice(index, 1);
            }
            else{
                //index = this.stepOneObject.trackingOption.indexOf(event.target.name);
                //if (index !== -1) this.stepOneObject.trackingOption.splice(index, 1);
            }
        }
    }

    render() {
        const { step } = this.state;
        return (
            <div>
                Main File
                <h2>Step {step}</h2>
                {this.showStepDetails()}
            </div>
        );
    };
}

export default MainStep;
